select , , , , , fi1.file_id fi1_id, fi1.file_name fi1_name, fi1.file_path fi1_path, fi1.description fi1_des, fi2.file_id fi2_id, fi2.file_name fi2_name, fi2.file_path fi2_path, fi2.description fi2_des, fi3.file_id fi3_id, fi3.file_name fi3_name, fi3.file_path fi3_path, fi3.description fi3_des from (select from alarm al left join lock_process lo on al.process_id=lo.process_id inner join ( select from role_lock_auth au
au.role_id in
#{role}
) au on (lo.lock_code = au.lock_code) ) al left join lock_process lo on al.process_id=lo.process_id left join file_info fi1 on lo.face_panorama_id=fi1.file_id left join file_info fi2 on lo.face_pic_id=fi2.file_id left join file_info fi3 on lo.face_match_id=fi3.file_id left join lock_info li on li.lock_code = lo.lock_code left join camera_device cd on li.lock_code = cd.lock_code left join role_lock_auth au on (li.lock_id = au.lock_id and li.lock_code = au.lock_code)
al.process_id=#{processId}

